from collections import Counter

def minimumPushes(word: str) -> int:
    d = Counter(word)
    freq = list(d.items())
    freq.sort(reverse=True, key=lambda x: x[1])
    remapped = 0
    pushes = 0
    for k,v in freq:
        pushes += v + (v*(remapped//8))
        remapped += 1
    return pushes

print(minimumPushes(""))